  --radius: .5rem;
  --shadow-2xs: 0px 1px 2px 0px hsl(0 0% 0% / 0.05);
  --shadow-xs: 0px 1px 3px 0px hsl(0 0% 0% / 0.08);
  --shadow-sm: 0px 2px 4px 0px hsl(0 0% 0% / 0.06), 0px 1px 2px -1px hsl(0 0% 0% / 0.06);
  --shadow: 0px 4px 6px 0px hsl(0 0% 0% / 0.07), 0px 2px 3px -1px hsl(0 0% 0% / 0.06);
  --shadow-md: 0px 6px 12px 0px hsl(0 0% 0% / 0.08), 0px 3px 5px -2px hsl(0 0% 0% / 0.07);
  --shadow-lg: 0px 10px 20px 0px hsl(0 0% 0% / 0.10), 0px 4px 8px -2px hsl(0 0% 0% / 0.08);
  --shadow-xl: 0px 16px 32px 0px hsl(0 0% 0% / 0.12), 0px 6px 12px -3px hsl(0 0% 0% / 0.10);
  --shadow-2xl: 0px 24px 48px 0px hsl(0 0% 0% / 0.15);
  --tracking-normal: 0em;
